d-article {
  padding-top: 5px;
  border-top: 0px;
}

d-title h1 {
  text-align: center;
  font-family: Roboto Mono;
  letter-spacing: 3px;
}

d-title p {
  text-align: center;
}

.about p {
  text-align: justify;
}

.hero-left img {
  width: 100%;
  border-radius: 15%;
  border: 3px solid grey;
  display: block;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 1em;
}

.hero-right p {
  margin: 10px 0px 10px 0px;
  font-size: 1.2em;
  font-family: Roboto Slab;
}

.hero-right li {
  margin-bottom: 5px;
  padding-left: 10px;
}

@media (min-width: 1200px) {
  .hero-left {
    width:32%;
    float:left;
  }
  .hero-right {
    width:63%;
    float:right;
  }
}

@media (max-width: 1199px) {
  .hero-left img {
    width: 50%;
  }
}






